import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import Web3 from "web3";
import contract from "./build/contracts/Auth.json";
class App extends Component {
  async componentWillMount() {
    console.log("componentWillMount");
    await this.loadWeb3();
  }

  async loadWeb3() {
    console.log("loadWeb3");
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    } else {
      window.alert(
        "Non-Ethereum browser detected. You should consider trying MetaMask!"
      );
      window.location.href =
        "https://chrome.google.com/webstore/detail/metamask/nkbihfbeogaeaoehlefnkodbefgpgknn";
    }
  }

  constructor(props) {
    super(props);
    this.state = {
      contract: contract,
      abi: contract.abi,
      address: "0x0",
      balance: "0",
      gasPrice: "0",
      gasLimit: "0",
      gasUsed: "0",
      txHash: "0x0",
      txReceipt: "0x0",
      txStatus: "0",
      txError: "0",
      txGasUsed: "0",
      txGasPrice: "0",
      txGasLimit: "0",
      txValue: "0",
      txInput: "0x0",
      txOutput: "0x0",
      txContract: "0x0",
      txContractAddress: "0x0",
      txContractFunction: "0x0",
      txContractFunctionAddress: "0x0",
      txContractFunctionReturn: "0x0",
      txContractFunctionReturnValue: "0x0",
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <form>
              <input
                style={{ width: "530px" }}
                className="footer-input"
                name="CurrentOwner"
                type="text"
                placeholder="Owner Address"
                value={this.state.CurrentOwner}
                onChange={this.handleChange}
              />
              <button>Submit</button>
            </form>
          </div>
        </header>
      </div>
    );
  }
}

export default App;
